import { useEffect, useState } from 'react';
import './addlist.css'; // Import your custom CSS file

const AddList = () => {
    const [postData1, setPostData1] = useState([]);

      useEffect(() => {
        fetchData();
      }, []);
    
      const fetchData = async () => {
        try {
          const response = await fetch('http://localhost:8000/getall',  
          {
            method: "GET",
          headers: {
              "Content-type": "application/json"},

              
          });
        // console.log(setPostData1)
        const data = await response.json();
        setPostData1(data);

        } catch (error) {
          console.log(error);
          alert('An error occurred while fetching post data.');
        }
      };
     
return (
   <>
   {postData1.map((post)=>(
    <>
    <div className="user-details-form" key={post.writeid}>
        <div className="user-profile">
          <h3>User Profile</h3>
          <p><strong>Username:</strong> {post.user_name}</p>
          <p><strong>Email:</strong> {post.email}</p>
          {/* Display the profile photo if available */}
          {post.profile_photo && (
            <img src={URL.createObjectURL(post.profile_photo)} alt="Profile" />
          )}
        </div>
    
    </div>
    </>


   ))}
   
    </>
  );
};

export default AddList;
