import React from 'react'
import {
  CBadge,
  CButton,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CDataTable,
  CRow
} from '@coreui/react'
import { DocsLink } from 'src/reusable'

import usersData from '../../views/users/UsersData'

const getBadge = status => {
  switch (status) {
    case 'Active': return 'success'
    case 'Inactive': return 'secondary'
    case 'Pending': return 'warning'
    case 'Banned': return 'danger'
    default: return 'primary'
  }
}

const fields = ['writeid ','post_title','postWrite','Photo_upload ','postDate', 'postCategory', 'user_id','role']

const BlogList = () => {
    const [write, setWrite] = useState({
        post_title: state ? state.post_title : '',
        Photo_upload: state ? state.Photo_upload : ''
        // postCategory: state ? 'postCategory' : '',

    })
    const handleDelete = (id) => {
        // Implement your delete logic here
        console.log(`Delete user with id: ${id}`);
      };
    
      const handleEdit = (id) => {
        // Implement your edit logic here
        console.log(`Edit user with id: ${id}`);
      };
    
      const handleSuspend = (id) => {
        // Implement your suspend logic here
        console.log(`Suspend user with id: ${id}`);
      };
      const HandleClick = async (e) => {
        e.preventDefault();
        const postDate = moment(Date.now()).format("YYYY-MM-DD");

        const user_id = currentUser.user_id;
        console.log(user_id)


        try { 
            const form = new FormData();
            form.append('post_title', write.post_title);
            form.append('postWrite', value1);
            form.append('Photo_upload', write.Photo_upload);
            form.append('postCategory', write.postCategory);
            form.append('postDate', postDate);
            // form.append('user_id', user_id);
            // form.append('writeid',state.writeid)

            
            const response =await fetch('http://localhost:8000/write', {
                    method: 'GET',
                    body: form,
                  })
                 
    
            if (response.ok) {
                // Handle success if needed
                console.log('Post created/updated successfully');
                alert("updated successfully")
            } else {
                // Handle error if needed
                console.error('Error creating/updating the post');
            }
         } catch (error) {
            console.log(error);
            alert('An error occurred while submitting the form.');    
            alert ("ERROR")
        }
    }
    
  return (
    <>
      
        <CRow>
        <CCol>
          <CCard>
            <CCardHeader>
             <h1 style={{textAlign:"center"}}>TOTAL BLOG LIST</h1>
             
            </CCardHeader>
            <CCardBody>
            <CDataTable
              items={usersData}
              fields={fields}
              
              dark
              hover
              striped
              bordered
              size="sm"
              itemsPerPage={10}
              pagination
              scopedSlots = {{
                'status':
                  (item)=>(
                    <td>
                      <CBadge color={getBadge(item.status)}>
                        {item.status}
                      </CBadge>
                    </td>
                  ),'role': (item) => (
                    <td>
                      <div style={{ display: 'flex', gap: '10px' }}>
                      <CButton color="danger" onClick={() => handleDelete(item.id)}>
                        Delete
                      </CButton>
                      <CButton color="warning" onClick={() => handleEdit(item.id)}>
                        Edit
                      </CButton>
                      <CButton color="info" onClick={() => handleSuspend(item.id)}>
                        Suspend
                      </CButton>
                      </div>
                    </td>
                  )
              }}
              
            
              
            />
            </CCardBody>
          </CCard>
        </CCol>
      </CRow>
    </>
  )
}

export default BlogList
