import React from 'react'
import {
  CBadge,
  CButton,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CDataTable,
  CRow
} from '@coreui/react'
import { DocsLink } from 'src/reusable'

import usersData from '../../views/users/UsersData'
import { useState } from 'react'
import { useEffect } from 'react'

const getBadge = status => {
  switch (status) {
    case 'Active': return 'success'
    case 'Inactive': return 'secondary'
    case 'Pending': return 'warning'
    case 'Banned': return 'danger'
    default: return 'primary'
  }
}

const fields = ['writeid ','post_title','postWrite','Photo_upload','postDate', 'postCategory', 'user_id','role']

const BlogList = () => {
    const [postData, setPostData] = useState([]);

    useEffect(() => {
      fetchData();
    }, []);
  
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:8000/getposts',  
        {method: "GET",
        headers: {
            "Content-type": "application/json"}
        });
        if (response.ok) {
          const data = await response.json();
          setPostData(data);
          
        } else {
          console.error('Error fetching post data from the API');
        }
      } catch (error) {
        console.log(error);
        alert('An error occurred while fetching post data.');
      }
    };

  
  return (
    <>
      
        <CRow>
        <CCol>
          <CCard>
            <CCardHeader>
             <h1 style={{textAlign:"center"}}>TOTAL BLOG LIST</h1>
             
            </CCardHeader>
            <CCardBody>
            <CDataTable
              items={postData}
              fields={fields}
              
              dark
              hover
              striped
              bordered
              size="sm"
              itemsPerPage={10}
              pagination
              scopedSlots={{
                'postDate': (item) => (
                  <td>
                    {new Date(item.postDate).toLocaleDateString()}
                  </td>
                ),
                'user_id': (item) => (
                  <td>
                    {item.user_id}
                  </td>
                ),
                'writeid': (item) => (
                  <td>
                    {item.writeid}
                  </td>
                ),
                'postCategory': (item) => (
                    <td>
                      {item.postCategory}
                    </td>
                  ),
                  'Photo_upload': (item) => (
                    <td>
                      {item.Photo_upload}
                    </td>
                  ),
                  'post_title': (item) => (
                    <td>
                      {item.post_title}
                    </td>
                  ),
                  
                'status': (item) => (
                    <td>
                      <CBadge color={getBadge(item.status)}>
                        {item.status}
                      </CBadge>
                    </td>
                  ),
                'role': (item) => (
                  <td>
                    {/* Add buttons here */}
                    <div style={{ display: 'flex', gap: '10px' }}>
                      <CButton color="danger" >
                        Delete
                      </CButton>
                      <CButton color="warning">
                        Edit
                      </CButton>
                      <CButton color="info" >
                        Suspend
                      </CButton>
                    </div>
                  </td>
                ),
              }}
              
            
              
            />
            </CCardBody>
          </CCard>
        </CCol>
      </CRow>
    </>
  )
}

export default BlogList
