import React from 'react'
import {
  CBadge,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CDataTable,
  CRow
} from '@coreui/react'
import { DocsLink } from 'src/reusable'

import usersData from '../../views/users/UsersData'

const getBadge = status => {
  switch (status) {
    case 'Active': return 'success'
    case 'Inactive': return 'secondary'
    case 'Pending': return 'warning'
    case 'Banned': return 'danger'
    default: return 'primary'
  }
}

const fields = ['user_name','password','email ','registered', 'role', 'status']

const UserList = () => {
    const images=[{
        userid:1,
        pic:"https://images.pexels.com/photos/4033244/pexels-photo-4033244.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"
    },
    {
        userid:2,
        pic:"https://images.pexels.com/photos/4033244/pexels-photo-4033244.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"
    },
    {
        userid:3,
        pic:"https://images.pexels.com/photos/4033244/pexels-photo-4033244.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"
    },
    {
        userid:4,
        pic:"https://images.pexels.com/photos/4033244/pexels-photo-4033244.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"
    },
]
  return (
    <>
