import { useState } from "react";


function AddList()
    {
        const [content, setContent] = useState('');
      
        const handleContentChange = (event) => {
          setContent(event.target.value);
        };
      
        const handleBoldClick = () => {
          setContent((prevContent) => prevContent + '**bold text**');
        };
      
        const handleItalicClick = () => {
          setContent((prevContent) => prevContent + '_italic text_');
        };
      
        const handleColorClick = () => {
          setContent((prevContent) => prevContent + '<span style="color: red;">colored text</span>');
        };
      
        const handleHeadingClick = (level) => {
          setContent((prevContent) => prevContent + `\n\n<h${level}>Heading ${level}</h${level}>\n\n`);
        };
      
        return (
          <div>
            <h1>Text Editor</h1>
            <div>
              <button onClick={handleBoldClick}>Bold</button>
              <button onClick={handleItalicClick}>Italic</button>
              <button onClick={handleColorClick}>Color</button>
              <button onClick={() => handleHeadingClick(1)}>Heading 1</button>
              <button onClick={() => handleHeadingClick(2)}>Heading 2</button>
              <button onClick={() => handleHeadingClick(3)}>Heading 3</button>
            </div>
            <textarea
              value={content}
              onChange={handleContentChange}
              rows={10}
              cols={50}
              style={{ fontFamily: 'monospace' }}
            />
            <div>
              <h2>Preview</h2>
              <div dangerouslySetInnerHTML={{ __html: content }} />
            </div>
          </div>
        );
     

  
}
export default AddList
