import { useState } from "react";


function AddList()
{
    const [code, setCode] = useState('');

  const handleCodeChange = (event) => {
    setCode(event.target.value);
  };

  return (
    <div>
      <h2>Code Editor</h2>
      <textarea
        value={code}
        onChange={handleCodeChange}
        rows={10}
        cols={50}
        style={{ fontFamily: 'monospace' }}
      />
    </div>
  );

  
}
export default AddList
